document.addEventListener("DOMContentLoaded", function() {
    // Define an array of background colors
    const colors = ["#ffb3b3", "#b3ffcc", "#b3b3ff", "#ffcc99"];

    // Get a random index to select a color from the array
    const randomIndex = Math.floor(Math.random() * colors.length);

    // Update the --background CSS variable with the selected color
    document.documentElement.style.setProperty("--background", colors[randomIndex]);
});
